#Family counter

#The function for the family counter
def family_counter():
    #The variables for the family counter
    boys = int(input("Enter number of boys: "))
    girls = int(input("Enter number of girls: "))
    #The print for the family counter
    print ("Wow thats a good sized family!")
    print(boys + girls)
    #The forever loop for the family counter
    while True:

        family_counter()
